  &reftitle.examples;



    Grundlegendes Beispiel für die Verwendung von bindto


<?php
// Verbindung zum Internet über die IP-Adresse '192.168.0.100' aufbauen
$opts = array(
    'socket' => array(
        'bindto' => '192.168.0.100:0',
    ),
);


// Verbindung zum Internet über die IP-Adresse '192.168.0.100' und den Port '7000' aufbauen
$opts = array(
    'socket' => array(
        'bindto' => '192.168.0.100:7000',
    ),
);


// Verbindung zum Internet über die IPv6-Adresse '2001:db8::1' aufbauen
// und Port '7000'
$opts = array(
    'socket' => array(
        'bindto' => '[2001:db8::1]:7000',
    ),
);


// Verbindung zum Internet über Port '7000' aufbauen
$opts = array(
    'socket' => array(
        'bindto' => '0:7000',
    ),
);


// den Kontext erstellen...
$context = stream_context_create($opts);

// ...und ihn zum Abrufen der Daten verwenden
echo file_get_contents('http://www.example.com', false, $context);

?>
