sect1.sect2:
   &reftitle.examples;

    Interface-Beispiel


<?php
// Deklariere das Interface 'iTemplate'
interface iTemplate
{
    public function setVariable($name, $var);
    public function getHtml($template);
}

// Implementiere das Interface
// Dies funktioniert
class Template implements iTemplate
{
    private $vars = array();

    public function setVariable($name, $var)
    {
        $this->vars[$name] = $var;
    }

    public function getHtml($template)
    {
        foreach($this->vars as $name => $value) {
            $template = str_replace('{' . $name . '}', $value, $template);
        }

        return $template;
    }
}

// Dies wird nicht funktionieren
// Fatal error: Class BadTemplate contains 1 abstract methods
// and must therefore be declared abstract (iTemplate::getHtml)
class BadTemplate implements iTemplate
{
    private $vars = array();

    public function setVariable($name, $var)
    {
        $this->vars[$name] = $var;
    }
}

?>




    Interface-Vererbung


<?php
interface a

public function foo();


interface b extends a
{
  public function baz(Baz $baz);
}

// Dies Funktioniert 
class c implements b
{
  public function foo()
  {
  }

  public function baz(Baz $baz)
  {
  }
}

// Dies funktioniert nicht und führt zu einem fatalen Fehler
class d implements b
{
  public function foo()
  {
  }

  public function baz(Foo $foo)
  {
  }
}
?>




    Interface-Mehrfachvererbung


<?php
interface a
{
  public function foo();
}

interface b
{
  public function bar();
}

interface c extends a, b
{
  public function baz();
}

class d implements c
{
  public function foo()
  {
  }

  public function bar()
  {
  }

  public function baz()
  {
  }
}
?>




    Interfaces mit Konstanten


<?php
interface a
{
  const b = 'Interface constant';
}

// Ausgabe: Interface constant
echo a::b;

// Der folgende Abschnitt wird nicht funktionieren, da
// ein Überschreiben der Konstanten nicht gestattet ist.
// Dies ist das gleiche Konzept wie bei Klassenkonstanten.
class b implements a
{
  const b = 'Class constant';
}
?>




