sect1.sect2:
   &reftitle.examples;

    Interface-Beispiel


<?php
// Deklariere das Interface 'Template'
interface Template
{
    public function setVariable($name, $var);
    public function getHtml($template);
}

// Implementiere das Interface
// Dies funktioniert
class WorkingTemplate implements Template
{
    private $vars = [];

    public function setVariable($name, $var)
    {
        $this->vars[$name] = $var;
    }

    public function getHtml($template)
    {
        foreach($this->vars as $name => $value) {
            $template = str_replace('{' . $name . '}', $value, $template);
        }

        return $template;
    }
}

// Dies wird nicht funktionieren
// Fatal error: Class BadTemplate contains 1 abstract methods
// and must therefore be declared abstract (Template::getHtml)
class BadTemplate implements Template
{
    private $vars = [];

    public function setVariable($name, $var)
    {
        $this->vars[$name] = $var;
    }
}
?>




    Erweiterbare Interfaces


<?php
interface A
{
    public function foo();
}

interface B extends A
{
    public function baz(Baz $baz);
}

// Dies Funktioniert
class C implements B
{
    public function foo()
    {
    }

    public function baz(Baz $baz)
    {
    }
}

// Dies funktioniert nicht und führt zu einem schwerwiegenden Fehler
class D implements B
{
    public function foo()
    {
    }

    public function baz(Foo $foo)
    {
    }
}
?>




    Interface-Mehrfachvererbung


<?php
interface A
{
    public function foo();
}

interface B
{
    public function bar();
}

interface C extends A, B
{
    public function baz();
}

class D implements C
{
    public function foo()
    {
    }

    public function bar()
    {
    }

    public function baz()
    {
    }
}
?>




    Interfaces mit Konstanten


<?php
interface A
{
    const B = 'Interface-Konstante';
}

// Ausgabe: Interface-Konstante
echo A::B;

// Der folgende Abschnitt wird nicht funktionieren, da
// ein Überschreiben der Konstanten nicht gestattet ist.
class B implements A
{
    const B = 'Class-Konstante';
}
?>




    Interfaces mit abstrakten Klassen


<?php
interface A
{
    public function foo(string $s): string;

    public function bar(int $i): int;
}

// Eine abstrakte Klasse muss ein Interface nicht komplett implementieren.
// Klassen, die die abstrakte Klasse erweitern, müssen den Rest implementieren.
abstract class B implements A
{
    public function foo(string $s): string
    {
        return $s . PHP_EOL;
    }
}

class C extends B
{
    public function bar(int $i): int
    {
        return $i * 2;
    }
}
?>




    Gleichzeitiges Erweitern und Implementieren


<?php

class One
{
    /* ... */
}

interface Usable
{
    /* ... */
}

interface Updatable
{
    /* ... */
}

// Die Reihenfolge der Schlüsselwörter ist hier wichtig. 'extends' muss an
// erster Stelle stehen.
class Two extends One implements Usable, Updatable
{
    /* ... */
}
?>





    Ein Interface bietet in Verbindung mit Typ-Deklarationen eine gute
    Möglichkeit, um sicherzustellen, dass ein bestimmtes Objekt bestimmte
    Methoden enthält. Siehe
    instanceof-Operator und
    Typdeklarationen.
