sect2.example:
    Einfaches Beispiel


<?php
// Deklariere eine einfache Klasse
class TestClass
{
    public $foo;

    public function __construct($foo) {
        $this->foo = $foo;
    }

    public function __toString() {
        return $this->foo;
    }
}

$class = new TestClass('Hallo');
echo $class;
?>


    &example.outputs;


Hallo
