para.example:
   Bitweise UND, ODER und XODER Operationen mit Ganzzahlen


<?php
/*
 * Der erste Abschnitt kann ignoriert werden;
 * er dient nur zu Formatierungszwecken, um die Ausgabe deutlicher zu machen.
 */

$format = '(%1$2d = %1$04b) = (%2$2d = %2$04b)'
      . ' %3$s (%4$2d = %4$04b)' . "\n";

echo <<<EOH
 ---------     ---------  -- ---------
 Ergebnis      Wert 1     Op Wert 2
 ---------     ---------  -- ---------
EOH;


/*
 * Hier kommen die Beispiele.
 */

$values = array(0, 1, 2, 4, 8);
$test = 1 + 4;

echo "\n Bitweises UND \n";
foreach ($values as $value) {
    $result = $value & $test;
    printf($format, $result, $value, '&', $test);
}

echo "\n Bitweises einschließendes ODER \n";
foreach ($values as $value) {
    $result = $value | $test;
    printf($format, $result, $value, '|', $test);
}

echo "\n Bitweises ausschließendes ODER (XODER) \n";
foreach ($values as $value) {
    $result = $value ^ $test;
    printf($format, $result, $value, '^', $test);
}
?>


   &example.outputs;


 ---------     ---------  -- ---------
 Ergebnis      Wert 1     Op Wert 2
---------     ---------  -- ---------
 Bitweises UND
( 0 = 0000) = ( 0 = 0000) & ( 5 = 0101)
( 1 = 0001) = ( 1 = 0001) & ( 5 = 0101)
( 0 = 0000) = ( 2 = 0010) & ( 5 = 0101)
( 4 = 0100) = ( 4 = 0100) & ( 5 = 0101)
( 0 = 0000) = ( 8 = 1000) & ( 5 = 0101)

 Bitweises einschließendes ODER
( 5 = 0101) = ( 0 = 0000) | ( 5 = 0101)
( 5 = 0101) = ( 1 = 0001) | ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) | ( 5 = 0101)
( 5 = 0101) = ( 4 = 0100) | ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) | ( 5 = 0101)

 Bitweises ausschließendes ODER (XODER)
( 5 = 0101) = ( 0 = 0000) ^ ( 5 = 0101)
( 4 = 0100) = ( 1 = 0001) ^ ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) ^ ( 5 = 0101)
( 1 = 0001) = ( 4 = 0100) ^ ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) ^ ( 5 = 0101)

Bit-Verschiebung mit Ganzzahlen
<?php /* * Hier kommen die Beispiele. */
echo "\n--- RECHTSVERSCHIEBUNG MIT POSITIVEN GANZZAHLEN ---\n";
$val = 4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'Kopie des Vorzeichenbits wird links hereingeschoben');
$val = 4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places);
$val = 4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'Bits werden rechts herausgeschoben');
$val = 4; $places = 4; $res = $val >> $places; p($res, $val, '>>', $places, 'dasselbe Ergebnis wie oben; man kann nicht über 0 hinaus verschieben');
echo "\n--- RECHTSVERSCHIEBUNG MIT NEGATIVEN GANZZAHLEN ---\n";
$val = -4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'Kopie des Vorzeichenbits wird links hereingeschoben');
$val = -4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places, 'Bits werden rechts herausgeschoben');
$val = -4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'dasselbe Ergebnis wie oben; man kann nicht über -1 hinaus verschieben');
echo "\n--- LINKSSVERSCHIEBUNG MIT POSITIVEN GANZZAHLEN ---\n";
$val = 4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'Nullen werden rechts hereingeschoben');
$val = 4; $places = (PHP_INT_SIZE * 8) - 4; $res = $val << $places; p($res, $val, '<<', $places);
$val = 4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places, 'Vorzeichenbits werden herausgeschoben');
$val = 4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'Bits werden links herausgeschoben');
echo "\n--- LINKSVERSCHIEBUNG MIT NEGATIVEN GANZZAHLEN ---\n";
$val = -4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'Nullen werden rechts hereingeschoben');
$val = -4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places);
$val = -4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'Bits werden links herausgeschoben, einschließlich des Vorzeichenbits');
/* * Dieser Abschnitt kann ignoriert werden; * es handlet sich nur um Formatierung, um die Ausgabe deutlicher zu machen. */
function p($res, $val, $op, $places, $note = '') { $format = '%0' . (PHP_INT_SIZE * 8) . "b\n";
printf("Ausdruck: %d = %d %s %d\n", $res, $val, $op, $places);
echo " Dezimal:\n"; printf(" Wert=%d\n", $val); printf(" Erg.=%d\n", $res);
echo " Binär:\n"; printf(' Wert=' . $format, $val); printf(' Erg.=' . $format, $res);
if ($note) { echo " ANMERKUNG: $note\n"; }
echo "\n"; } ?>
&example.outputs.32bit;
