partintro.section:
   &reftitle.examples;

    Erzeugt durch eine Typumwandlung in ein Objekt


<?php
$obj = (object) array('foo' => 'bar');
var_dump($obj);


    &example.outputs;


object(stdClass)#1 (1) {
  ["foo"]=>
  string(3) "bar"
}




    Erzeugt mittels json_decode


<?php
$json = '{"foo":"bar"}';
var_dump(json_decode($json));


    &example.outputs;


object(stdClass)#1 (1) {
  ["foo"]=>
  string(3) "bar"
}




    Deklarieren dynamischer Eigenschaften


<?php
$obj = new stdClass();
$obj->foo = 42;
$obj->{1} = 42;
var_dump($obj);


    &example.outputs;


object(stdClass)#1 (2) {
  ["foo"]=>
  int(42)
  ["1"]=>
  int(42)
}
