    &Methods;








   &reftitle.examples;



     Weakmap Anwendungsbeispiel


<?php
$wm = new WeakMap();

$o = new StdClass;

class A {
    public function __destruct() {
        echo "Zerstört!\n";
    }
}

$wm[$o] = new A;

var_dump(count($wm));
echo "Entferne...\n";
unset($o);
echo "Fertig\n";
var_dump(count($wm));


     &example.outputs;


int(1)
Entferne...
Zerstört!
Fertig
int(0)









