para.example:
      array_unique example


<?php
$input = array("a" => "grün", "rot", "b" => "grün", "blau", "rot");
$result = array_unique($input);
print_r($result);
?>


      &example.outputs;


Array
(
    [a] => grün
    [0] => rot
    [1] => blau
)

      array_unique und Typen


<?php
$input = array(4,"4","3",4,3,"3");
$result = array_unique($input);
var_dump($result);
?>


      &example.outputs;


array(2) {
  [0] => int(4)
  [2] => string(1) "3"
}
