para.example:
      uksort


function cmp($a, $b)
{
    if ($a == $b) {
        return 0;
    }
    return ($a > $b) ? -1 : 1;
}

$a = array(4 => "vier", 3 => "drei", 20 => "zwanzig", 10 => "zehn");

uksort($a, "cmp");

foreach ($a as $key => $value) {
    echo "$key: $value\n";
}


      &example.outputs;


20: zwanzig
10: zehn
4: vier
3: drei
