para.example:
      uksort


function cmp($a, $b)
{
    $a = ereg_replace('^(ein|eine|die) ', '', $a);
    $b = ereg_replace('^(ein|eine|die) ', '', $b);
	return strcasecmp($a, $b);
}

$a = array("John" => 1, "die Erde" => 2, "ein Apfel" => 3, "eine Banane" => 4);

uksort($a, "cmp");

foreach ($a as $key => $value) {
    echo "$key: $value\n";
}


      &example.outputs;


ein Apfel: 3
eine Banane: 4
die Erde: 2
John: 1
