  Ein kleines bzip2-Beispiel


<?php

$filename = "/tmp/testfile.bz2";
$str = "Das ist ein Test.\n";

// Datei zum Schreiben Ã¶ffnen
$bz = bzopen($filename, "w");

// Zeichenkette in Datei schreiben
bzwrite($bz, $str);

// Datei schlieÃen
bzclose($bz);

// Datei zum Lesen Ã¶ffnen
$bz = bzopen($filename, "r");

// 10 Zeichen lesen
echo bzread($bz, 10);

/* Ausgabe bis zum Dateiende (oder der nÃ¤chsten 1024 Zeichen) und schlieÃen
der Datei. */
echo bzread($bz);

bzclose($bz);

?>
