refentry.refsect1:
    &reftitle.examples;



      bzread-Beispiel


<?php

$file = "/tmp/foo.bz2";
$bz = bzopen($file, "r") or die("Konnte $file nicht öffnen");

$decompressed_file = '';
while (!feof($bz)) {
  $decompressed_file .= bzread($bz, 4096);
}
bzclose($bz);

echo "Der Inhalt von $file ist: <br />\n";
echo $decompressed_file;

?>
