refentry.refsect1:
&reftitle.parameters;




   clear_realpath_cache



     Ob der realpath Zwischenspeicher geleert werden soll.




   filename



     Leere den realpath und den Stat Zwischenspeicher nur für einen bestimmten Dateinamen;
     wird nur genutzt, wenn clear_realpath_cache &true; ist.

  &reftitle.examples;



    clearstatcache Beispiel


<?php
$file = 'output_log.txt';

function get_owner($file)
{
    $stat = stat($file);
    $user = posix_getpwuid($stat['uid']);
    return $user['name'];
}

$format = "UID @ %s: %s\n";

printf($format, date('r'), get_owner($file));

chown($file, 'ross');
printf($format, date('r'), get_owner($file));

clearstatcache();
printf($format, date('r'), get_owner($file));
?>


    &example.outputs.similar;


UID @ Sun, 12 Oct 2008 20:48:28 +0100: root
UID @ Sun, 12 Oct 2008 20:48:28 +0100: root
UID @ Sun, 12 Oct 2008 20:48:28 +0100: ross
