refsect1.para:
     Gibt bei Erfolg den neuen temporären Dateinamen bei Erfolg, im
     Fehlerfalle &false; zurück.

      tempnam


$tmpfname = tempnam ("/tmp", "FOO");

$handle = fopen($tmpfname, "w");
fwrite($handle, "schreibe in Tämporärdatei");
fclose($handle);

// tue hier etwas

unlink($tmpfname);
