refentry.refsect1:
&reftitle.description;
boolftp_rename resourceftp_stream stringoldname stringnewname
ftp_rename benennt eine Datei oder ein Verzeichnis auf dem FTP_Server um.

  &reftitle.examples;



    ftp_rename-Beispiel


<?php
$old_file = 'somefile.txt.bak';
$new_file = 'somefile.txt';

// Verbindung aufbauen
$conn_id = ftp_connect($ftp_server);

// Login mit Benutzername und Passwort
$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass);

// Versuche $old_file in $new_file umzubenennen
if (ftp_rename($conn_id, $old_file, $new_file)) {
 echo "$old_file erfolgreich in $new_file umbenannt\n";
} else {
 echo "Ein Fehler trat beim Umbenennen von $old_file in $new_file auf\n";
}

// Verbindung schlieÃŸen
ftp_close($conn_id);
?>
