refentry.refsect1:
  &reftitle.examples;



    Prozedurales gnupg_geterrorinfo-Beispiel


<?php
$res = gnupg_init();
// dies wird ohne Fehler aufgerufen
print_r(gnupg_geterrorinfo($res));
?>


    &example.outputs;


array(4) {
  ["generic_message"]=>
  bool(false)
  ["gpgme_code"]=>
  int(0)
  ["gpgme_source"]=>
  string(18) "Unspecified source"
  ["gpgme_message"]=>
  string(7) "Success"
}




    Objektorientiertes gnupg_geterrorinfo-Beispiel


<?php
$gpg = new gnupg();
// fehlerhafter Aufruf
$gpg->decrypt('abc');
// die Fehlerinformationen sollten angezeigt werden
print_r($gpg->geterrorinfo());
?>


    &example.outputs;


array(4) {
  ["generic_message"]=>
  string(14) "decrypt failed"
  ["gpgme_code"]=>
  int(117440570)
  ["gpgme_source"]=>
  string(5) "GPGME"
  ["gpgme_message"]=>
  string(7) "No data"
}
