refentry.refsect1:
&reftitle.parameters;



  &gd.image.description;

   x



     x-Kooordinate des Punkts.




   y



     y-Koordinate des Punkts.

  &reftitle.examples;



    Zurgriff auf individuelle RGB-Werte


<?php
$im = imagecreatefrompng("php.png");
$rgb = imagecolorat($im, 10, 15);
$r = ($rgb >> 16) & 0xFF;
$g = ($rgb >> 8) & 0xFF;
$b = $rgb & 0xFF;

var_dump($r, $g, $b);
?>


    &example.outputs.similar;


int(119)
int(123)
int(180)




    Menschenlesbare RGB-Werte unter Verwendung von imagecolorsforindex


<?php
$im = imagecreatefrompng("php.png");
$rgb = imagecolorat($im, 10, 15);

$colors = imagecolorsforindex($im, $rgb);

var_dump($colors);
?>


    &example.outputs.similar;


array(4) {
  ["red"]=>
  int(119)
  ["green"]=>
  int(123)
  ["blue"]=>
  int(180)
  ["alpha"]=>
  int(127)
}
