refentry.refsect1:
&reftitle.description;
resourceimagecreatefrompng stringfilename
imagecreatefrompng gibt den Bezeichner auf ein Bild zurück, der das aus der angegebenen Datei eingelesene Bild darstellt.
&tip.fopen-wrapper;

  &reftitle.examples;

   Beispiel für Fehlerbehandlung beim Laden eines PNG


<?php
function LoadPNG($imgname)
{
    /* Versuche zu öffnen */
    $im = @imagecreatefrompng($imgname);

    /* Prüfe, ob das fehlschlug */
    if(!$im)
    {
        /* Erzeuge ein schwarzes Bild */
        $im  = imagecreatetruecolor(150, 30);
        $bgc = imagecolorallocate($im, 255, 255, 255);
        $tc  = imagecolorallocate($im, 0, 0, 0);

        imagefilledrectangle($im, 0, 0, 150, 30, $bgc);

        /* Gib eine Fehlermeldung aus */
        imagestring($im, 1, 5, 5, 'Fehler beim Laden von ' . $imgname, $tc);
    }

    return $im;
}

header('Content-Type: image/png');

$img = LoadPNG('bogus.image');

imagepng($img);
imagedestroy($img);
?>


