example.programlisting:
$mbox = imap_open ("{your.imap.host:143}", "username", "password")
    || die ("can't connect: " . imap_last_error());

$overview = imap_fetch_overview ($mbox, "2,4:6", 0);

if (is_array ($overview )) {
    reset ($overview);
    while (list ($key, $val) = each ($overview)) {
        print $val->msgno
            . " - " . $val->date
            . " - " . $val->subject
            . "\n";
    }
}

imap_close ($mbox);
