refentry.refsect1:
&reftitle.description;
arrayimap_getmailboxes resourceimap_stream stringref stringpattern
Liefert Postfachinformationen.

&reftitle.parameters;



  &imap.imap-stream.description;

   ref



     Spezifikation des gewünschten Servers wie in 
     imap_open beschrieben.




   pattern

    &imap.pattern;

  &reftitle.examples;



    imap_getmailboxes Beispiel


<?php
$mbox = imap_open("{imap.example.org}", "username", "password", OP_HALFOPEN)
      or die("kann nicht verbinden: " . imap_last_error());

$list = imap_getmailboxes($mbox, "{imap.example.org}", "*");
if (is_array($list)) {
    foreach ($list as $key => $val) {
        echo "($key) ";
        echo imap_utf7_decode($val->name) . ",";
        echo "'" . $val->delimiter . "',";
        echo $val->attributes . "<br />\n";
    }
} else {
    echo "imap_getmailboxes schlug fehl: " . imap_last_error() . "\n";
}

imap_close($mbox);
?>
