refentry.refsect1:
  &reftitle.examples;



    Ein einfaches Beispiel um zurückgegebene ini-Dateien aufzulisten


<?php
if ($dateiliste = php_ini_scanned_files()) {
    if (strlen($dateiliste) > 0) {
        $dateien = explode(',', $dateiliste);

        foreach ($dateien as $datei) {
            echo "<li>" . trim($datei) . "</li>\n";
        }
    }
}
?>
