refentry.refsect1:
  &reftitle.description;

&style.oop;

   public objectfalsemysqli_result::fetch_field_direct
   intindex


&style.procedural;

   objectfalsemysqli_fetch_field_direct
   mysqli_resultresult
   intindex



   Gibt ein Objekt zurück, das die Informationen über die Definition einer
   Spalte aus der angegebenen Ergebnismenge enthält.

&reftitle.parameters;



  &mysqli.result.description;

   index



     Die Nummer der Spalte. Dieser Wert muss im Bereich von
     0 bis Anzahl der Spalten - 1
     liegen.

  &reftitle.examples;

   &style.oop;


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Verbindung überprüfen */
if (mysqli_connect_errno()) {
    printf("Verbindung fehlgeschlagen: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Name LIMIT 5";

if ($result = $mysqli->query($query)) {

    /* Informationen über die Spalte 'SurfaceArea' abrufen */
    $finfo = $result->fetch_field_direct(1);

    printf("Name:       %s\n", $finfo->name);
    printf("Tabelle:    %s\n", $finfo->table);
    printf("max. Länge: %d\n", $finfo->max_length);
    printf("Flags:      %d\n", $finfo->flags);
    printf("Typ:        %d\n", $finfo->type);

    $result->close();
}

/* Verbindung schließen */
$mysqli->close();
?>




   &style.procedural;


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Verbindung überprüfen */
if (mysqli_connect_errno()) {
    printf("Verbindung fehlgeschlagen: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Name LIMIT 5";

if ($result = mysqli_query($link, $query)) {

    /* Informationen über die Spalte 'SurfaceArea' abrufen */
    $finfo = mysqli_fetch_field_direct($result, 1);

    printf("Name:       %s\n", $finfo->name);
    printf("Tabelle:    %s\n", $finfo->table);
    printf("max. Länge: %d\n", $finfo->max_length);
    printf("Flags:      %d\n", $finfo->flags);
    printf("Typ:        %d\n", $finfo->type);

    mysqli_free_result($result);
}

/* Verbindung schließen */
mysqli_close($link);
?>


   &examples.outputs;


Name:       SurfaceArea
Tabelle:    Country
max. Länge: 10
Flags:      32769
Typ:        4
