refentry.refsect1:
  &reftitle.description;

&style.oop;

   public objectfalsemysqli_result::fetch_field



&style.procedural;

   objectfalsemysqli_fetch_field
   mysqli_resultresult



   Gibt die Definition einer Spalte aus einer Ergebnismenge als Objekt zurück.
   Um Informationen über alle Spalten in der Ergebnismenge abzurufen, muss
   diese Funktion mehrmals aufgerufen werden.

  &reftitle.examples;

   &style.oop;


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Verbindung überprüfen */
if (mysqli_connect_errno()) {
    printf("Verbindung fehlgeschlagen: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = $mysqli->query($query)) {

    /* Informationen über alle Spalten abrufen */
    while ($finfo = $result->fetch_field()) {

        printf("Name:       %s\n", $finfo->name);
        printf("Tabelle:    %s\n", $finfo->table);
        printf("max. Länge: %d\n", $finfo->max_length);
        printf("Flags:      %d\n", $finfo->flags);
        printf("Typ:        %d\n\n", $finfo->type);
    }
    $result->close();
}

/* Verbindung schließen */
$mysqli->close();
?>




   &style.procedural;


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Verbindung überprüfen */
if (mysqli_connect_errno()) {
    printf("Verbindung fehlgeschlagen: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = mysqli_query($link, $query)) {

    /* Informationen über alle Spalten abrufen */
    while ($finfo = mysqli_fetch_field($result)) {

        printf("Name:       %s\n", $finfo->name);
        printf("Tabelle:    %s\n", $finfo->table);
        printf("max. Länge: %d\n", $finfo->max_length);
        printf("Flags:      %d\n", $finfo->flags);
        printf("Typ:        %d\n\n", $finfo->type);
    }
    mysqli_free_result($result);
}

/* Verbindung schließen */
mysqli_close($link);
?>


   &examples.outputs;


Name:       Name
Tabelle:    Country
max. Länge: 11
Flags:      1
Typ:        254

