      &oracle.deprecated;




     3.0.1

      Die Konstanten für Typ hinzugefügt. In früheren
      Versionen sollten Sie die numerischen Werte verwenden.

  &reftitle.examples;



    ora_bind-Beispiel


<?php
  ora_parse($curs, "declare tmp INTEGER; begin tmp := :in; :out := tmp; :x := 7.77; end;");
  ora_bind($curs, "ergebnis", ":x", $len, 2);
  ora_bind($curs, "eingabe", ":in", 5, 1);
  ora_bind($curs, "ausgabe", ":out", 5, 2);
  $eingabe = 765;
  ora_exec($curs);
  echo "Ergebnis: $ergebnis<br />Ausgabe: $ausgabe<BR>Eingabe: $eingabe";
?>
