refentry.refsect1:
&reftitle.description;

 arrayob_list_handlers




 Listet alle verwendeten Ausgabe-Handler auf.

  &reftitle.examples;



    ob_list_handlers-Beispiel


<?php
// Mit output_buffering=On, kein Ausgabe-Handler gesetzt
var_dump(ob_list_handlers());
ob_end_flush();

// Kein Callback oder null
ob_start();
var_dump(ob_list_handlers());
ob_end_flush();

// Anonyme Funktion
ob_start(function($string) { return $string; });
var_dump(ob_list_handlers());
ob_end_flush();

// Pfeilfunktion
ob_start(fn($string) => $string);
var_dump(ob_list_handlers());
ob_end_flush();

// Callback-Funktion als Objekt erster Klasse
$firstClassCallable = userDefinedFunction(...);

ob_start([$firstClassCallable, '__invoke']);
var_dump(ob_list_handlers());
ob_end_flush();

// Interne (eingebaute) Funktion
ob_start('print_r');
var_dump(ob_list_handlers());
ob_end_flush();

// Benutzerdefinierte Funktion
function userDefinedFunction($string, $flags) { return $string; };

ob_start('userDefinedFunction');
var_dump(ob_list_handlers());
ob_end_flush();

class MyClass {
    public static function staticHandle($string) {
        return $string;
    }

    public static function handle($string) {
        return $string;
    }

    public function __invoke($string) {
        return $string;
    }
}

// Klasse und statische Methode
ob_start(['MyClass','staticHandle']);
var_dump(ob_list_handlers());
ob_end_flush();

// Objekt und nicht-statische Methode
ob_start([new MyClass,'handle']);
var_dump(ob_list_handlers());
ob_end_flush();

// Aufrufbares Objekt
ob_start(new MyClass);
var_dump(ob_list_handlers());
ob_end_flush();
?>


