refentry.refsect1:
  &reftitle.examples;

     Öffnen und extrahieren


<?php
$zip = new ZipArchive;
$res = $zip->open('test.zip');
if ($res === TRUE) {
    echo 'ok';
    $zip->extractTo('test');
    $zip->close();
} else {
    echo 'Fehler, Code:' . $res;
}
?>




     Ein Archiv erstellen


<?php
$zip = new ZipArchive;
$res = $zip->open('test.zip', ZipArchive::CREATE);
if ($res === TRUE) {
    $zip->addFromString('test.txt', 'Der Dateiinhalt kommt hier');
    $zip->addFile('data.txt', 'eintragsname.txt');
    $zip->close();
    echo 'ok';
} else {
    echo 'Fehler';
}
?>




     Erstellen eines temporären Archivs


<?php
$name = tempnam(sys_get_temp_dir(), "FOO");
$zip = new ZipArchive;
$res = $zip->open($name, ZipArchive::OVERWRITE); /* trunkieren, da eine leere Datei nicht gültig ist */
if ($res === TRUE) {
    $zip->addFile('data.txt', 'eintragsname.txt');
    $zip->close();
    echo 'ok';
} else {
    echo 'Fehler';
}
?>
